not.sqlCheck:
select count(*)
from patient
where date_created < (select min(date_created) from event_records where category = 'patient');

changeSet.sql:
INSERT INTO event_records(uuid,title,timestamp,object,uri,category,tags)
select uuid(),'Patient',now(),CONCAT('/openmrs/ws/rest/v1/patient/',uuid, '?v=full'),NULL,'patient','patient'
from person where date_created < (select min(date_created) from event_records where category = 'patient');

